import PySimpleGUI as sg
from Bio import SeqIO
from Bio.Seq import Seq
from Bio.SeqRecord import SeqRecord
from Bio.Align import MultipleSeqAlignment
import Bio.Seq     
from collections import Counter
import re

sg.theme('DarkGray13')   # Add a touch of color
# All the stuff inside your window.
layout = [  [sg.Text('SARS_CoV_2-Fitness')],
            [sg.Text('Enter accession number of COVID sequence'), sg.InputText()],
            [sg.Text('Enter nucleotide sequence [note: enter only the nucleotides! Not the name or entire FASTA format.]'), sg.InputText()],
            [sg.Button('Click to truncate sequnce to the spike protein and translate!'), sg.Button('Cancel')] ]

# Create the Window
window = sg.Window('Window Title', layout,size=(700, 150))
# Event Loop to process "events" and get the "values" of the inputs
while True:
    event, values = window.read()
    if event == sg.WIN_CLOSED or event == 'Cancel': # if user closes window or clicks cancel
        break
    name_of_file = "{file}.fasta".format(file = values[0])
    
#     print('You entered ', values[0])
#     print('You entered ', values[1])
#     sg.Popup(event, values, line_width=200)
    
#Cuts SARS-CoV-2 genome down to nt 21563:25384 (around spike protein and some flanking nucleotides)
     
    with open('fasta_sequences.fasta', 'w') as seq:
        seq.write(">NC_045512.2\n")
        seq.write("")
        seq.write(str('\n\n>{}\n'.format(values[0])))
        seq.write(str(values[1]))
    
      #Good place to obtain sequences for testing:
    """https://www.ncbi.nlm.nih.gov/labs/virus/vssi/#/virus?SeqType_s=Nucleotide&VirusLineage_ss=Severe%
    20acute%20respiratory%20syndrome%20coronavirus%202%20(SARS-CoV-2),
    %20taxid:2697049&CollectionDate_dr=1950-01-01T00:00:00Z%20TO%20NOW&CreateDate_dt=
    1950-01-01T00:00:00Z%20TO%20NOW&BaselineSurveillance_s=only&Completeness_s=complete"""

    fasta=SeqIO.parse("fasta_sequences.fasta","fasta")

    seq_dict={}
    for record in fasta:
            seq_dict[record.id]=record.seq

    excised_dict = []

    # print(seq_dict.values())
    #This function truncates each of the 200 sequences and saves the output as new_fasta_file2
    
    def excision(fasta_seq):

        n = -1

        for i in range(len(fasta_seq.values())):

            n = n + 1
            
    #For semi-whole genome:
    #         fasta_line = list(fasta_seq.values())[n][14463:28484]
    #For spike:
            fasta_line = list(fasta_seq.values())[n][21563:25384]

            cut_dash = re.sub('-', '', str(fasta_line))

            ident = '>' + str(list(fasta_seq.keys())[n])
            seq = [ident + '\n' + cut_dash ]

            excised_dict.append(*seq)

        with open('new_fasta_file_var.fasta', 'w') as new_fasta:
            
            for individ_fasta in excised_dict:
                
    #             new_fasta.write("\n" + str(individ_fasta.keys()) + "\n" + str(individ_fasta.values()) + "\n")
                new_fasta.write(str(individ_fasta) + '\n')

            new_fasta.close()
        return (None)

    excision(seq_dict)

#Aligns the truncated sequencs:
    #-------------------------
  
    seqs = []
    with open('new_fasta_file_var.fasta','r') as reads:
        for line in reads:
            seqs.append(line)

    _seqs_to_be_aln = seqs[1::2]
    seqs_to_be_aln = []
    [seqs_to_be_aln.append(Seq("{}\n".format(seq[0:-1]))) for seq in _seqs_to_be_aln]
    names = seqs[0::2]

    a = SeqRecord(seqs_to_be_aln[0], id ='{}'.format(names[0]))
    b = SeqRecord(seqs_to_be_aln[1], id ='{}'.format(names[1]))

    align = MultipleSeqAlignment([a, b])

    #File we reference later in code to translate
    with open('aligned_omi.fasta', 'w') as aligned:
        for record in align:
            aligned.write("{}".format(record.id))

            aligned.write("\n{}\n".format(record.seq))
            
            
            
            
#Translates the truncated sequence            
    #------------------------
    
    final_translated_seqs = {}
  

    fasta=SeqIO.parse("aligned_omi.fasta","fasta")
    seq_dict={}

    for record in fasta:
            seq_dict[record.id]=record.seq
    # print(seq_dict)

    def translate(AA):
        ref = ''
        _possible = {}

        seq = str(AA).replace('-','')
        if seq == 'exit': exit()
        rev_seq = Bio.Seq.reverse_complement(seq)

        # Forward 1
        f1 = Bio.Seq.translate(seq, gap = None)
        _possible['f1'] = f1
        # Forward 2
        f2 = Bio.Seq.translate(seq[1:],gap = None)
        _possible['f2'] = f2

        # Forward 3
        f3 = Bio.Seq.translate(seq[2:], gap = None)
        _possible['f3'] = f3


        # Reverse 1
        r1 = Bio.Seq.translate(rev_seq, gap = None)
        _possible['r1'] = r1
        # Reverse 2
        r2 = Bio.Seq.translate(rev_seq[1:],gap = None)
        _possible['r2'] = r2
        # Reverse 3
        r3 = Bio.Seq.translate(rev_seq[2:], gap = None)
        _possible['r3'] = r3

        vals = list(_possible.values())
        ref = Counter("""""")

        index = []

        for tran in list(_possible.values()):

            v1 = Counter(tran)

            #The lower the number the more conserved.
            v1.subtract(ref)

            diff = (list(v1.values()))
            diff = [abs(i) for i in diff]

            index.append(sum(diff))

        min_value = min(index)
        min_index = index.index(min_value)
        best = list(_possible.keys())[min_index]
        best_translated_seq = _possible[best]
        return best_translated_seq


    for sequence in list(seq_dict):
        seq_val = seq_dict[sequence]
        seq = translate(seq_val)
        final_translated_seqs[sequence] = seq

    final_translated_seqs

    with open('ori_final_translated_seqs.fasta', 'w') as tran:
        for line in final_translated_seqs:
            tran.write("\n>{line}\n.".format(line=line))
            tran.write("{}\n".format(final_translated_seqs[line]))

    translated_seq = []        
    with open('ori_final_translated_seqs.fasta', 'r') as tran:
        for line in tran:
            translated_seq.append(line)
    
    with open('{}'.format(name_of_file), 'w') as output_file:
        for line in translated_seq[5][2:-2]:
            output_file.write(line)
            
    sg.popup_scrolled("""Copy and paste the translated sequence below into a file and load that file into SpikePro. 
    In order to install SpikePro, use a terminal with c++ compiler compatibility (i.e., Ubuntu) and enter the following to install SpikePro:
    
    For first time users:
    c++ SpikePro.cpp edlib/src/edlib.cpp CSVparser.cpp -o SpikePro -I edlib/include/ -std=c++11    
    
    
    Run the following code: 
    
    ./SpikePro {file} go
    
    {translated}""".format(translated = translated_seq[5][2:-2], file =name_of_file),size = (150,30))

window.close()
